                <div class="mb-6 mt-4 py-6 bg-white border border-gray-200 rounded-md shadow">
                    <x-site-in-list :site="$site"></x-site-in-list>
                </div>
            @endforeach

            <h2 class="mt-10 font-semibold text-2xl">Parked</h2>
            @foreach ($parked as $site)
                <div class="mb-6 mt-4 py-6 bg-white border border-gray-200 rounded-md shadow">
                    <x-site-in-list :site="$site"></x-site-in-list>
                </div>
